// src/App.js
import React, { useState } from 'react';
import './App.css';

const App = () => {
  const imagens = [
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT9C1YQbyFYAod6dO9XJE1TTDUMxL6aijhwZyRlZE5Pyw&s',
    'https://placeimg.com/640/480/animals',
    'https://placeimg.com/640/480/arch',
    'https://placeimg.com/640/480/nature',
    // Adicione mais URLs de imagens conforme necessário
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    const newIndex = currentIndex === imagens.length - 1 ? 0 : currentIndex + 1;
    setCurrentIndex(newIndex);
  };

  const prevSlide = () => {
    const newIndex = currentIndex === 0 ? imagens.length - 1 : currentIndex - 1;
    setCurrentIndex(newIndex);
  };

  return (

<div class="slide-show" >
  <ul class="slides-list" >
    <li class="slide" >
      <img src="https://picsum.photos/300/300?image=8" />
    </li>
    <li class="slide" >
      <img src="https://picsum.photos/300/300?image=0" />
    </li>
    <li class="slide" >
      <img src="https://picsum.photos/300/300?image=6" />
    </li>
    <li class="slide" >
      <img src="https://picsum.photos/300/300?image=1" />
    </li>
  </ul>
</div>
  );
};

export default App;
