import { useEffect, useState, useRef } from 'react';
import './App.css';

function App() {
  const [data, setData] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const carousel = useRef(null);

  useEffect(() => {
    fetch('https://raw.githubusercontent.com/emersonbroga/super-shoes/main/public/static/shoes.json')
      .then((response) => response.json())
      .then(setData);
  }, []);

  const handleLeftClick = (e) => {
    e.preventDefault();
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  const handleRightClick = (e) => {
    e.preventDefault();
    if (currentIndex < data.length - 1) {
      setCurrentIndex(currentIndex + 1);
    }
  };

  const handleDotClick = (index) => {
    setCurrentIndex(index);
  };

  if (!data || data.length === 0) return null;

  return (
    <div className="container">
      <div className="logo">
        <img src="/static/images/super-shoes.png" alt="Super Shoes Logo" />
      </div>
      <div className="carousel-container">
        <div className="carousel" ref={carousel}>
          {data.map((item, index) => {
            const { id, name, price, oldPrice, image } = item;
            return (
              <div className={`item ${index === currentIndex ? 'active' : ''}`} key={id}>
                <div className="image">
                  <img src={image} alt={name} />
                </div>
                <div className="info">
                  <span className="name">{name}</span>
                  <span className="oldPrice">U$ {oldPrice}</span>
                  <span className="price">U$ {price}</span>
                </div>
              </div>
            );
          })}
        </div>
        <div className="buttons">
          <button className="left-button" onClick={handleLeftClick} disabled={currentIndex === 0}>
            <span> ⬅️ </span>
          </button>
          <button className="right-button" onClick={handleRightClick} disabled={currentIndex === data.length - 1}>
            <span> ➡️ </span>
          </button>
        </div>
        <div className="dots">
          {data.map((item, index) => (
            <span
              key={index}
              className={`dot ${index === currentIndex ? 'active' : ''}`}
              onClick={() => handleDotClick(index)}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
