// src/components/Carrossel.js
import React, { useState } from 'react';
import './carrossel.css';

const Carrossel = ({ imagens }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    const newIndex = currentIndex === imagens.length - 1 ? 0 : currentIndex + 1;
    setCurrentIndex(newIndex);
  };

  const prevSlide = () => {
    const newIndex = currentIndex === 0 ? imagens.length - 1 : currentIndex - 1;
    setCurrentIndex(newIndex);
  };

  return (
    <div className="carrossel">
      <button onClick={prevSlide} className="controle anterior">&#10094;</button>
      {imagens.map((imagem, index) => (
        <div
          key={index}
          className={index === currentIndex ? 'slide ativo' : 'slide'}
          style={{ backgroundImage: `url(${imagem})` }}
        ></div>
      ))}
      <button onClick={nextSlide} className="controle proximo">&#10095;</button>
    </div>
  );
};

export default Carrossel;
