import { useEffect, useState, useRef } from 'react';
import './App.css';

function App() {
  const [data, setData] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const carousel = useRef(null);

  useEffect(() => {
    fetch('https://raw.githubusercontent.com/emersonbroga/super-shoes/main/public/static/shoes.json')
      .then((response) => response.json())
      .then(setData);
  }, []);

  const handleLeftClick = (e) => {
    e.preventDefault();
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  const handleRightClick = (e) => {
    e.preventDefault();
    if (currentIndex < data.length - 1) {
      setCurrentIndex(currentIndex + 1);
    }
  };

  if (!data || data.length === 0) return null;

  const { id, name, price, oldPrice, image } = data[currentIndex];

  return (
    <div className="container">
      <div className="logo">
        <img src="/static/images/super-shoes.png" alt="Super Shoes Logo" />
      </div>
      <div className="carousel" ref={carousel}>
        <div className="item" key={id}>
          <div className="image">
            <img src={image} alt={name} />
          </div>
          <div className="info">
            <span className="name">{name}</span>
            <span className="oldPrice">U$ {oldPrice}</span>
            <span className="price">U$ {price}</span>
          </div>
        </div>
      </div>
      <div className="buttons">
        <button onClick={handleLeftClick} disabled={currentIndex === 0}>
          <span> ⬅️ </span>
        </button>
        <button onClick={handleRightClick} disabled={currentIndex === data.length - 1}>
          <span> ➡️ </span>
        </button>
      </div>
    </div>
  );
}

export default App;
