import React, { useEffect, useRef, useState } from "react";
import "@fortawesome/fontawesome-free/css/all.css";
import "./Slider.css";

const Slider = ({ slidesData, autoPlay = true, interval = 3000 }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const [currentX, setCurrentX] = useState(0);
  const [isAutoPlay, setIsAutoPlay] = useState(autoPlay);
  const slideListRef = useRef(null);
  const intervalRef = useRef(null);

  useEffect(() => {
    if (isAutoPlay) {
      intervalRef.current = setInterval(() => {
        setCurrentSlide((prev) => (prev + 1) % slidesData.length);
      }, interval);
    }

    return () => clearInterval(intervalRef.current);
  }, [isAutoPlay, interval, slidesData.length]);

  useEffect(() => {
    const slideList = slideListRef.current;
    const handleTransitionEnd = () => {
      if (currentSlide === slidesData.length) {
        setCurrentSlide(0);
        slideList.style.transition = "none";
        slideList.style.transform = `translateX(0px)`;
      } else if (currentSlide === -1) {
        setCurrentSlide(slidesData.length - 1);
        slideList.style.transition = "none";
        slideList.style.transform = `translateX(-${(slidesData.length - 1) * 100}%)`;
      } else {
        slideList.style.transition = "transform .5s";
        slideList.style.transform = `translateX(-${currentSlide * 100}%)`;
      }
    };

    slideList.addEventListener("transitionend", handleTransitionEnd);
    return () => slideList.removeEventListener("transitionend", handleTransitionEnd);
  }, [currentSlide, slidesData.length]);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slidesData.length);
  };

  const previousSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slidesData.length) % slidesData.length);
  };

  const handleControlClick = (index) => {
    setCurrentSlide(index);
  };

  const handleMouseDown = (event) => {
    setIsDragging(true);
    setStartX(event.clientX);
    setCurrentX(event.clientX);
    slideListRef.current.style.transition = "none";
  };

  const handleMouseMove = (event) => {
    if (!isDragging) return;
    setCurrentX(event.clientX);
    const moveX = currentX - startX;
    const slideList = slideListRef.current;
    slideList.style.transform = `translateX(calc(-${currentSlide * 100}% + ${moveX}px))`;
  };

  const handleMouseUp = () => {
    setIsDragging(false);
    const moveX = startX - currentX;
    if (moveX > 100) {
      nextSlide();
    } else if (moveX < -100) {
      previousSlide();
    } else {
      setCurrentSlide(currentSlide);
    }
  };

  const handleTouchStart = (event) => {
    setIsDragging(true);
    setStartX(event.touches[0].clientX);
    setCurrentX(event.touches[0].clientX);
    slideListRef.current.style.transition = "none";
  };

  const handleTouchMove = (event) => {
    if (!isDragging) return;
    setCurrentX(event.touches[0].clientX);
    const moveX = currentX - startX;
    const slideList = slideListRef.current;
    slideList.style.transform = `translateX(calc(-${currentSlide * 100}% + ${moveX}px))`;
  };

  const handleTouchEnd = () => {
    setIsDragging(false);
    const moveX = startX - currentX;
    if (moveX > 100) {
      nextSlide();
    } else if (moveX < -100) {
      previousSlide();
    } else {
      setCurrentSlide(currentSlide);
    }
  };

  const handleMouseEnter = () => {
    clearInterval(intervalRef.current);
  };

  const handleMouseLeave = () => {
    if (isAutoPlay) {
      intervalRef.current = setInterval(() => {
        setCurrentSlide((prev) => (prev + 1) % slidesData.length);
      }, interval);
    }
  };

  return (
    <div className="wrapper">
      <div className="slide-wrapper">
        <div
          className="slide-list"
          ref={slideListRef}
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onMouseLeave={handleMouseUp}
          onTouchStart={handleTouchStart}
          onTouchMove={handleTouchMove}
          onTouchEnd={handleTouchEnd}
        >
          {slidesData.map((slide, index) => (
            <div key={index} className="slide-item">
              <div className="slide-content">
                <img src={slide.src} alt={slide.alt} className="slide-image" />
                <div className="slide-description">
                  <h3>{slide.title}</h3>
                  <p>{slide.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
        <button className="slide-nav-button slide-nav-previous" onClick={previousSlide}>
          <i className="fas fa-chevron-left"></i>
        </button>
        <button className="slide-nav-button slide-nav-next" onClick={nextSlide}>
          <i className="fas fa-chevron-right"></i>
        </button>
        <div className="slide-controls">
          {slidesData.map((_, index) => (
            <button
              key={index}
              className={`slide-control-button ${currentSlide === index ? "active" : ""}`}
              onClick={() => handleControlClick(index)}
            >
              <i className="fas fa-circle"></i>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Slider;
