// src/App.js
import React, { useState } from 'react';
import './App.css';

const App = () => {
  const imagens = [
    'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT9C1YQbyFYAod6dO9XJE1TTDUMxL6aijhwZyRlZE5Pyw&s',
    'https://placeimg.com/640/480/animals',
    'https://placeimg.com/640/480/arch',
    'https://placeimg.com/640/480/nature',
    // Adicione mais URLs de imagens conforme necessário
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    const newIndex = currentIndex === imagens.length - 1 ? 0 : currentIndex + 1;
    setCurrentIndex(newIndex);
  };

  const prevSlide = () => {
    const newIndex = currentIndex === 0 ? imagens.length - 1 : currentIndex - 1;
    setCurrentIndex(newIndex);
  };

  return (
    <div className="App">
      <h1>Slider Simples</h1>
      <div className="slider">
        <button onClick={prevSlide} className="controle anterior">&#10094;</button>
        <img src={imagens[currentIndex]} alt="slide" className="slide" />
        <button onClick={nextSlide} className="controle proximo">&#10095;</button>
      </div>
    </div>
  );
};

export default App;
